import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import logo from "./assets/logo1.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserCheck,
  faCalendarCheck
} from "@fortawesome/free-solid-svg-icons";
import { Calendar } from "@fullcalendar/core";
import interactionPlugin from "@fullcalendar/interaction";
import dayGridPlugin from "@fullcalendar/daygrid";
import esLocale from "@fullcalendar/core/locales/es";
import swal from "sweetalert";
import Animate from "animate.css-react";
import $ from "jquery";
import "@fullcalendar/core/main.css";
import "@fullcalendar/daygrid/main.css";
import "animate.css/animate.css";
import "./App.css";

class App extends Component {
  state = {
    step_no: "1 - ",
    step_content: "Elige el día de tu visita",
    dating: {
      date: "",
      name: "",
      tel: "",
      email: "",
      age: "",
      city: ""
    },
    un_dates: []
  };
  componentDidMount() {
    fetch("https://webservicesnt.org:4443/kidzania", {
      method: "POST",
      body: JSON.stringify({ type: "get", data: {} }),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(res => res.json())
      .then(result => {
        this.setState({
          un_dates: result.data
        });
      });
    $(".input-effect input").focusout(function() {
      if ($(this).val() !== "") {
        $(this).addClass("has-content");
      } else {
        $(this).removeClass("has-content");
      }
    });

    const that = this;
    var calendarEl = document.getElementById("calendar-cont");
    var calendar = new Calendar(calendarEl, {
      plugins: [dayGridPlugin, interactionPlugin],
      locale: esLocale,
      defaultView: $(window).width() < 765 ? "dayGridWeek" : "dayGridMonth",
      selectable: true,
      droppable: true,
      dateClick: function(info) {
        let dating = that.state.dating;
        if (dating.date != "") {
          $("[data-date='" + dating.date + "']").removeClass("fc-highlight");
        }
        $("[data-date='" + dating.date + "']").addClass("fc-highlight");
        dating.date = info.dateStr;
        that.setState({ dating: dating });
        //let event = calendar.getEventById("myvisit");
        //if (event != null) {
        //  event.remove();
        //}
        //calendar.addEvent({
        //  id: "myvisit",
        //  title: "Mi visita",
        //  start: info.dateStr,
        //  allDay: true
        //});
        console.log(info.dateStr);
      }
    });
    calendar.render();
  }
  changeStep = (curr_step, e) => {
    e.preventDefault();
    switch (curr_step) {
      case 1:
        if (this.state.dating.date === "") {
          swal({
            title: "¡Espera!",
            text: "Olvidaste seleccionar una fecha",
            icon: "warning"
          });
        } else {
          this.setState({ step_no: "2 -" });
          this.setState({ step_content: "Cuéntanos un poco sobre ti" });
        }
        break;
      case 2:
        break;
      default:
        break;
    }
  };
  render() {
    return (
      <>
        <Row className="nav">
          <Col xs="12">
            <Row>
              <Col xs="3" sm="3" md="2" lg="1">
                <img className="kz-nav-logo" src={logo} />
              </Col>
              <Col xs="9" sm="9" md="10" lg="11">
                <span className="nav-item">Agendar mi visita</span>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row>
          <Col xs="12" lg={{ size: 10, offset: 1 }}>
            <p className="date-title">
              Completa los siguientes pasos y reserva tu lugar ¡Ahora!
            </p>
            <div className="stepper-cont">
              <div className="stepper-line" />
              <div className="stepper-step">
                <div className="stepper-rad">
                  <FontAwesomeIcon icon={faCalendarCheck} />
                </div>
              </div>
              <div className="stepper-step">
                <div className="stepper-rad">
                  <FontAwesomeIcon icon={faUserCheck} />
                </div>
              </div>
            </div>
            <div className="body-cont">
              <p className="date-subtitle">
                {this.state.step_no}
                <span className="date-content">{this.state.step_content}</span>
              </p>
              <div id="calendar-cont" />
              <Row className="row-div">
                <Col xs="12" className="row-div-r">
                  <button
                    className="kz-btn"
                    onClick={e => this.changeStep(1, e)}
                  >
                    Continuar
                  </button>
                </Col>
              </Row>
            </div>
            <div className="body-cont">
              <p className="date-subtitle">
                {this.state.step_no}
                <span className="date-content">{this.state.step_content}</span>
              </p>
              <form onSubmit={e => this.changeStep(2, e)}>
                <Row>
                  <Col sm="12" className="form-row">
                    <label className="kz-label" htmlFor="name">
                      ¿Cuál es tu nombre?
                      <span className="required-field">*</span>
                    </label>
                    <input
                      className="kz-input"
                      type="text"
                      pattern="([A-z0-9À-ž\s]){2,30}"
                      name="name"
                      required
                    />
                  </Col>
                </Row>
                <Row>
                  <Col sm="12" lg="6" className="form-row">
                    <label className="kz-label" htmlFor="age">
                      ¿Cuántos años tienes?
                      <span className="required-field">*</span>
                    </label>
                    <input
                      className="kz-input"
                      type="number"
                      max="99"
                      min="1"
                      name="age"
                      required
                    />
                  </Col>
                  <Col sm="12" lg="6" className="form-row">
                    <label className="kz-label" htmlFor="city">
                      ¿En qué ciudad te encuentras?
                      <span className="required-field">*</span>
                    </label>
                    <input
                      className="kz-input"
                      type="text"
                      maxLength="50"
                      pattern="([A-z0-9À-ž\s]){2,30}"
                      name="city"
                      required
                    />
                  </Col>
                </Row>
                <Row>
                  <Col sm="12" className="form-row">
                    <label className="kz-label" htmlFor="email">
                      ¿Cuál es tu correo electrónico
                      <span className="required-field">*</span>
                    </label>
                    <input
                      className="kz-input"
                      type="email"
                      name="email"
                      required
                    />
                  </Col>
                </Row>
                <Row>
                  <Col sm="12" className="form-row">
                    <label className="kz-label" htmlFor="tel">
                      ¿Cuál es tu número telefónico?
                      <span className="required-field">*</span>
                    </label>
                    <input
                      className="kz-input"
                      type="tel"
                      pattern="[0-9 -]{9,15}"
                      name="tel"
                      required
                    />
                  </Col>
                </Row>
                <Row>
                  <Col sm="12" className="form-row">
                    <label className="kz-label" htmlFor="comment">
                      Déjanos algún comentario adicional
                    </label>
                    <textarea
                      className="kz-input"
                      type="text"
                      name="comment"
                      maxLength="250"
                      rows="3"
                      placeholder="Escribe aquí lo que quieras"
                    />
                  </Col>
                </Row>
                <Row className="row-div">
                  <Col xs="12" className="row-div-r">
                    <button className="kz-btn" type="submit">
                      Continuar
                    </button>
                  </Col>
                </Row>
              </form>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default App;
