import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import logo from "./assets/logo1.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserCheck,
  faCalendarCheck
} from "@fortawesome/free-solid-svg-icons";
import { Calendar } from "@fullcalendar/core";
import interactionPlugin from "@fullcalendar/interaction";
import dayGridPlugin from "@fullcalendar/daygrid";
import esLocale from "@fullcalendar/core/locales/es";
import swal from "sweetalert";
import Animate from "animate.css-react";
import $ from "jquery";
import "@fullcalendar/core/main.css";
import "@fullcalendar/daygrid/main.css";
import "animate.css/animate.css";
import "./App.css";

class App extends Component {
  state = {
    today: new Date(),
    loading: true,
    dating: {
      date: "",
      name: "",
      tel: "",
      email: "",
      age: "",
      city: ""
    },
    un_dates: []
  };
  componentDidMount() {
    const that = this;
    fetch("https://webservicesnt.org:4443/kidzania", {
      method: "POST",
      body: JSON.stringify({ type: "get", data: {} }),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(res => res.json())
      .then(result => {
        if (result.result === 1) {
          this.setState({
            loading: false,
            un_dates: result.data
          });
        } else {
          swal({
            title: "¡Lo sentimos!",
            text:
              "Ha ocurrido un error al cargar las fechas, porfavor reintenta",
            icon: "error"
          }).then(function() {
            window.location.reload();
          });
        }
      });

    var calendarEl = document.getElementById("calendar-cont");
    console.log(this.state.un_dates);
    var calendar = new Calendar(calendarEl, {
      plugins: [dayGridPlugin, interactionPlugin],
      locale: esLocale,
      events: this.state.un_dates,
      validRange: {
        start: this.formatDate(this.state.today)
      },
      defaultView: $(window).width() < 765 ? "dayGridWeek" : "dayGridMonth",
      selectable: true,
      droppable: true,
      dateClick: function(info) {
        let dating = that.state.dating;
        if (dating.date != "") {
          $("[data-date='" + dating.date + "']").removeClass("fc-highlight");
        }
        dating.date = info.dateStr;
        that.setState({ dating: dating });
        $("[data-date='" + dating.date + "']").addClass("fc-highlight");
        //let event = calendar.getEventById("myvisit");
        //if (event != null) {
        //  event.remove();
        //}
        //calendar.addEvent({
        //  id: "myvisit",
        //  title: "Mi visita",
        //  start: info.dateStr,
        //  allDay: true
        //});
      }
    });
    calendar.render();
  }
  animateStep = n => {
    window.scrollTo(0, 0);
    $(".stepper-rad:eq(" + (n - 1) + ")").addClass("completed-step");
    let old_el = $(".body-cont:eq(" + (n - 1) + ")");
    let new_el = $(".body-cont:eq(" + n + ")");
    setTimeout(function() {
      old_el.css("display", "none");
      new_el.css("display", "block");
      new_el.css("opacity", 1);
    }, 500);
    old_el.css("opacity", 0);
    new_el.css("opacity", 0);
  };
  reverseStep = n => {
    let new_el = $(".body-cont:eq(" + (n - 1) + ")");
    let old_el = $(".body-cont:eq(" + n + ")");
    setTimeout(function() {
      old_el.css("display", "none");
      new_el.css("display", "block");
      new_el.css("opacity", 1);
      window.scrollTo(0, 0);
      $(".stepper-rad:eq(" + (n - 1) + ")").removeClass("completed-step");
    }, 500);
    old_el.css("opacity", 0);
    new_el.css("opacity", 0);
  };
  changeStep = (curr_step, e) => {
    e.preventDefault();
    switch (curr_step) {
      case 1:
        if (this.state.dating.date === "") {
          swal({
            title: "¡Espera!",
            text: "Olvidaste seleccionar una fecha",
            icon: "warning"
          });
        } else {
          this.animateStep(curr_step);
        }
        break;
      case 2:
        this.getFormData($("#kz-form"));
        fetch("https://webservicesnt.org:4443/kidzania", {
          method: "POST",
          body: JSON.stringify({ type: "get", data: this.state.dating }),
          headers: {
            "Content-Type": "application/json"
          }
        })
          .then(res => res.json())
          .then(result => {
            if (result.result === 1) {
              swal({
                title: "¡Excelente!",
                text: "Tu cita ha sido registrada correctamente",
                icon: "success"
              }).then(function() {
                //window.location.reload();
              });
            } else {
              swal({
                title: "¡Lo sentimos!",
                text:
                  "Ha ocurrido un error durante el registro, porfavor reintenta",
                icon: "error"
              });
            }
          });
        break;
      default:
        break;
    }
  };
  getFormData = form => {
    var unindexed_array = form.serializeArray();
    var indexed_array = this.state.dating;
    $.map(unindexed_array, function(n, i) {
      indexed_array[n["name"]] = n["value"];
    });
    this.setState({ dating: indexed_array });
  };
  formatDate = date => {
    var d = date,
