import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import logo from "./assets/logo.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Calendar } from "@fullcalendar/core";
import dayGridPlugin from "@fullcalendar/daygrid";
import "./App.css";

class App extends Component {
  state = {
    step_no: "1 - ",
    step_content: "Elige el día de tu visita"
  };
  render() {
    return (
      <>
        <Row className="nav">
          <Col xs="12">
            <Row>
              <Col xs="6" sm="6" md="2">
                <img src={logo} />
              </Col>
              <Col xs="6" sm="6" md="10">
                <span>Agendar mi visita</span>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row>
          <Col xs="12" lg={{ size: 10, offset: 1 }}>
            <p className="date-title">
              Completa los siguientes pasos y reserva tu lugar ¡Ahora!
            </p>
            <div className="stepper-cont">
              <div className="stepper-line" />
              <div className="stepper-step">
                <FontAwesomeIcon icon={["far", "calendar-check"]} />
              </div>
              <div className="stepper-step">
                <FontAwesomeIcon icon={["fas", "user-check"]} />
              </div>
            </div>
            <p className="date-subtitle">
              {this.state.step_no}
              <span className="date-content">{this.state.step_content}</span>
            </p>
            <div className="body-cont">
              <form>
                <div id="calendar-cont" />
                <button className="kz-btn">Continuar</button>
              </form>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default App;
