import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import logo from "./assets/logo1.svg";
import drag from "./assets/logo2.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserCheck,
  faCalendarCheck
} from "@fortawesome/free-solid-svg-icons";
import { Calendar } from "@fullcalendar/core";
import interactionPlugin from "@fullcalendar/interaction";
import dayGridPlugin from "@fullcalendar/daygrid";
import esLocale from "@fullcalendar/core/locales/es";
import swal from "sweetalert";
import Animate from "animate.css-react";
import "@fullcalendar/core/main.css";
import "@fullcalendar/daygrid/main.css";
import "animate.css/animate.css";
import "./App.css";

class App extends Component {
  state = {
    step_no: "1 - ",
    step_content: "Elige el día de tu visita",
    step1: false,
    dating: {
      date: "",
      name: "",
      tel: "",
      email: "",
      age: "",
      city: ""
    }
  };
  componentDidMount() {
    const that = this;
    var calendarEl = document.getElementById("calendar-cont");
    var calendar = new Calendar(calendarEl, {
      plugins: [dayGridPlugin, interactionPlugin],
      locale: esLocale,
      defaultView: "dayGridMonth",
      selectable: true,
      droppable: true,
      dateClick: function(info) {
        let dating = that.state.dating;
        dating.date = info.dateStr;
        that.setState({ dating: dating });
        console.log("clicked on " + info.dateStr);
        console.log(that.state);
      }
    });
    //calendar.select( { this.state.} )
    calendar.render();
  }
  changeStep = curr_step => {
    switch (curr_step) {
      case 1:
        if (this.state.dating.date === "") {
          swal({
            title: "¡Espera!",
            text: "Olvidaste seleccionar una fecha",
            icon: "warning"
          });
        } else {
          this.setState({ step_no: "2 -" });
          this.setState({ step_content: "Cuéntanos un poco sobre ti" });
        }
        break;
    }
  };
  render() {
    return (
      <>
        <Row className="nav">
          <Col xs="12">
            <Row>
              <Col xs="6" sm="6" md="2" lg="1">
                <img className="kz-nav-logo" src={logo} />
              </Col>
              <Col xs="6" sm="6" md="10" lg="11">
                <span className="nav-item">Agendar mi visita</span>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row>
          <Col xs="12" lg={{ size: 10, offset: 1 }}>
            <p className="date-title">
              Completa los siguientes pasos y reserva tu lugar ¡Ahora!
            </p>
            <div className="stepper-cont">
              <div className="stepper-line" />
              <div className="stepper-step">
                <div className="stepper-rad">
                  <FontAwesomeIcon icon={faCalendarCheck} />
                </div>
              </div>
              <div className="stepper-step">
                <div className="stepper-rad">
                  <FontAwesomeIcon icon={faUserCheck} />
                </div>
              </div>
            </div>
            <div className="body-cont">
              <p className="date-subtitle">
                {this.state.step_no}
                <span className="date-content">{this.state.step_content}</span>
              </p>
              <div id="calendar-cont" />
              <Row className="row-div">
                <Col xs="12" className="row-div-r">
                  <button className="kz-btn" onClick={e => this.changeStep(1)}>
                    Continuar
                  </button>
                </Col>
              </Row>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default App;
