import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import logo from "./assets/logo1.svg";
import drag from "./assets/logo2.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUserCheck,
  faCalendarCheck
} from "@fortawesome/free-solid-svg-icons";
import { Calendar } from "@fullcalendar/core";
import interactionPlugin from "@fullcalendar/interaction";
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/core/main.css";
import "@fullcalendar/daygrid/main.css";
import "./App.css";

class App extends Component {
  state = {
    step_no: "1 - ",
    step_content: "Elige el día de tu visita"
  };
  componentDidMount() {
    let draggableEl = document.getElementById("draggable");
    var calendarEl = document.getElementById("calendar-cont");
    var calendar = new Calendar(calendarEl, {
      plugins: [dayGridPlugin, interactionPlugin],
      defaultView: "dayGridMonth",
      selectable: true,
      droppable: true,
      dateClick: function() {
        alert("a day has been clicked!");
      }
    });
    calendar.render();
    let Draggable = new Draggable(draggableEl);
  }
  render() {
    return (
      <>
        <Row className="nav">
          <Col xs="12">
            <Row>
              <Col xs="6" sm="6" md="2" lg="1">
                <img className="kz-nav-logo" src={logo} />
              </Col>
              <Col xs="6" sm="6" md="10" lg="11">
                <span className="nav-item">Agendar mi visita</span>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row>
          <Col xs="12" lg={{ size: 10, offset: 1 }}>
            <p className="date-title">
              Completa los siguientes pasos y reserva tu lugar ¡Ahora!
            </p>
            <div className="stepper-cont">
              <div className="stepper-line" />
              <div className="stepper-step">
                <div className="stepper-rad">
                  <FontAwesomeIcon icon={faCalendarCheck} />
                </div>
              </div>
              <div className="stepper-step">
                <div className="stepper-rad">
                  <FontAwesomeIcon icon={faUserCheck} />
                </div>
              </div>
            </div>
            <div className="body-cont">
              <p className="date-subtitle">
                {this.state.step_no}
                <span className="date-content">{this.state.step_content}</span>
              </p>
              <form>
                <div id="calendar-cont" />
                <Row className="row-div">
                  <Col xs="6" className="row-div-l" id="draggable">
                    <img className="calendar-drag-img" src={drag} />
                  </Col>
                  <Col xs="6" className="row-div-r">
                    <button className="kz-btn">Continuar</button>
                  </Col>
                </Row>
              </form>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default App;
